import React from 'react'
import ReactApexChart from 'react-apexcharts'
import { useState } from 'react';
import pic from "./files.jpg";
import Bargraph from './Bargraph';

function Charts() {
    const [checker, setChecker] = useState(false)
    const [city, setCity] = useState(null)
    const [month, setMonth] = useState(null)
    const [od, setOd] = useState(null)
    const [oid, setOid] = useState(null)
    const [pe, setPe] = useState(null)
    const [prod, setProd] = useState(null)
    const [pa, setPa] = useState(null)
    const [qo, setQo] = useState(null)
    const [sales, setSales] = useState(null)
    const [type1, settype1] = useState('scatter')
    const [buttontype, setButtontype] = useState('0')

  function upload(e){

          e.preventDefault();
          const files = e.target.files
          const endpoint = "http://127.0.0.1:8000/domain_visualizations/sales";        
          const formData = new FormData();
          formData.append("userInput", 'satyam');
          formData.append('upfiles', files[0]);

          fetch(endpoint, {
              method: 'POST',
              body: formData,
              }
          ).then(response => response.json())
          .then(data => {
                  console.log(Object.values(data.results['hours sales']));
                  setCity(Object.values(data.results['hours sales'].City))
                  setMonth(Object.values(data.results['hours sales'].Month))
                  setOd(Object.values(data.results['hours sales'].Product))
                  setOid(Object.values(data.results['hours sales'].Product))
                  setPe(Object.values(data.results['hours sales'].Product))
                  setProd(Object.values(data.results['hours sales'].Product))
                  setPa(Object.values(data.results['hours sales'].Product))
                  setQo(Object.values(data.results['hours sales'].Product))
                  setSales(Object.values(data.results['hours sales'].Sales))
                  setChecker(true);
              }
          )
          .catch(error => {
                  console.error("Error: ", error);
              }
          );
      }
  function handelbar(){
    setButtontype('0');
  }
  function handelpie(){
    setButtontype('1');
