import './App.css';
import ReactApexChart from 'react-apexcharts'
import { useState } from 'react';

function App() {

  const [type1, settype1] = useState('scatter')

  let series= [{
    name: 'Hours Sale',
    data: [378,
    244,
    132,
    85,
    84,
    143,
    254,
    384,
    643,
    869,
     1134,
     1201,
     1201,
     1192,
     1112,
     986,
     1054,
     1013,
     1227,
     1286,
     1201,
     1018,
     849,
     599
    ]}, {
    name: 'Revenue',
    data: [378,
    244,
    132,
    85,
    84,
    143,
    254,
    384,
    643,
    869,
     1134,
     1201,
     1201,
     1192,
     1112,
     986,
     1054,
     1013,
     1227,
     1286,
     1201,
     1018,
     849,
     599
  ]
  }, {
    name: 'Free Cash Flow',
    data: [35, 41, 36, 26, 45, 48, 52, 53, 41]
  }]
  let options= {
  chart: {
    height: 350,
    zoom: {
      enabled: false
    }
  },
  dataLabels: {
    enabled: false
  },
  stroke: {
    curve: 'straight'
  },
  title: {
    text: 'Product Trends by Month',
    align: 'left'
  },
  grid: {
    row: {
      colors: ['#f3f3f3', 'transparent'], // takes an array which will be repeated on columns
      opacity: 0.5
    },
  },
  xaxis: {
    categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep'],
  }
}

function handelbar(){
  settype1('bar');

}
function handelpie(){
  settype1('bar');
}
function handeldonut(){
  settype1('bar');
}
function handelline(){
  settype1('line');
}
function handelscatter(){
  settype1('scatter');
}

  return (
    <div className="App">
        <div className="app_body">
          <div className="buttons">
            <div className="bar">
                <button onClick={handelbar}>Bar</button>
            </div>
            <div className="pie">
                <button onClick={handelbar}>Pie</button>
            </div>
            <div className="donut">
                <button onClick={handelbar}>Donut</button>
            </div>
            <div className="line">
                <button onClick={handelline}>Line</button>
            </div>
            <div className="dots">
                <button onClick={handelscatter}>Scatter</button>
            </div>
          </div>
          <div className="chart">
            <ReactApexChart options={options} series={series} type={type1} height={350} />          
          </div>
        </div>
    </div>
  );
}

export default App;
